import java.util.LinkedHashMap;import java.util.List;import java.util.Map;/** * Contains all Named Entity Recognition information for a single body of text. */public class NamedEntities {  private LinkedHashMap<String, Entity> namedEntities;  /**   * Constructor   * <p>   *  Initializes collection to hold NER information where   *  an Entity object contains a noun, its classified entity,   *  and its sentence number.   * </p>   */  protected NamedEntities() {    this.namedEntities = new LinkedHashMap<String, Entity>();  }  protected void add(final String noun, final String ner, final int sentenceNumber) {    this.namedEntities.put(noun, new Entity(ner, sentenceNumber));  }  protected String getEntity(final String noun) {    return this.namedEntities.get(noun).getEntity();  }  protected List<Integer> getSentenceNumbers(final String noun) {    return this.namedEntities.get(noun).getSentenceNumbers();  }  @Override  public String toString() {    StringBuilder output = new StringBuilder();    for (Map.Entry<String, Entity> pair : this.namedEntities.entrySet()) {      String name = pair.getKey();      Entity entity = pair.getValue();      output.append(name);      output.append(" -> ");      output.append(entity.getEntity());      output.append(" (sentence number: ");      output.append(entity.getSentenceNumbers());      output.append(")\n");    }    return output.append("\n").toString();  }}